mapper.update:
UPDATE COL
	SET CSTATS=#{cStats}
		WHERE CID=#{cId}

mapper.select:
SELECT SQL_CALC_FOUND_ROWS CID, CNAME, CEMAIL, CPNUM 
	FROM COL 
		WHERE CID LIKE CONCAT('%',#{keyword},'%') 
			ORDER BY CID DESC
		LIMIT #{NUM_OF_MSG_PER_PAGE}
		OFFSET #{START_ROW}

SELECT SQL_CALC_FOUND_ROWS CID, CNAME, CEMAIL, CPNUM 
	FROM COL 
		WHERE CID LIKE CONCAT('%',#{keyword},'%') 
			ORDER BY CID DESC
		LIMIT #{NUM_OF_MSG_PER_PAGE}
		OFFSET #{START_ROW}
